mapper.select:
select seq, fk_email, name, content, spoint, regDate, store_id, status, likecnt, cntcomments, parentSeq, depthno, groupno
from ( select rownum AS rno , seq, fk_email, name, content, spoint, regDate, store_id, status, likecnt, cntcomments, parentSeq, depthno, groupno from ( select seq, fk_email, name, content, spoint, regDate, store_id, status, likecnt, cntcomments, parentSeq, depthno, groupno from tbl_comment where status = 1 and store_id = #{code} start with parentSeq = 0 connect by prior seq = parentSeq order siblings by groupno desc, seq asc
) V ) T where rno between 1 and 10


 select no, type, kakaoid, name, email, pwd, to_char(registerday, 'yyyy-mm-dd'), status, idle, thumbnail_image      
 from tbl_member
 where email = #{email} and pwd = #{pwd}


 select no, type, kakaoid, name, email, pwd, registerday, status, idle, thumbnail_image      
 from tbl_member
 where  kakaoid = #{kakaoid}

  
select trunc( months_between(sysdate, max(logindate)) ) AS lastlogingap
 from tbl_loginhistory 
 where fk_email = #{email}

 	select count(*) 
